// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/Strings.sol";

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "@openzeppelin/contracts/utils/Strings.sol";

import "../HyperAGI_Roles_Cfg.sol";

import "./Agent_721.sol";
import "./Agent_1155.sol";

import "../utils/StrUtil.sol";
import "../HyperAGI_Roles_Cfg.sol";
import "../HyperAGI_Transaction_Cfg.sol";
import "../HyperAGI_Wallet_Account.sol";
import "../agent/HyperAGI_Agent.sol";

contract Agent_Mint is OwnableUpgradeable {
    address public _agentAddress;
    address public _walletAccountAddress;
    address public _transactionCfgAddress;
    address public _agentPOPNFTAddress;
    address public _rolesCfgAddress;

    using Strings for *;
    using StrUtil for *;

    function initialize(address onlyOwner) public initializer {
        __Ownable_init(onlyOwner);
    }

    function setAgentAddress(address agentAddress) public onlyOwner {
        _agentAddress = agentAddress;
    }

    function setWalletAccountAddress(address walletAccountAddress) public onlyOwner {
        _walletAccountAddress = walletAccountAddress;
    }

    function setTransactionCfgAddress(address transactionCfgAddress) public onlyOwner {
        _transactionCfgAddress = transactionCfgAddress;
    }

    function setAgentPOPNFTAddress(address agentPOPNFTAddress) public onlyOwner {
        _agentPOPNFTAddress = agentPOPNFTAddress;
    }

    function setRolesCfgAddress(address rolesCfgAddress) public onlyOwner {
        _rolesCfgAddress = rolesCfgAddress;
    }

    function setContractAddress(address[] memory contractaddressArray) public onlyOwner {
        _agentAddress = contractaddressArray[0];
        _walletAccountAddress = contractaddressArray[1];
        _transactionCfgAddress = contractaddressArray[2];
        _agentPOPNFTAddress = contractaddressArray[3];
        _rolesCfgAddress = contractaddressArray[4];
    }

    function mint721(bytes32 sid, string memory tokenURI) public payable {
        HyperAGI_Wallet_Account walletAccountAddress = HyperAGI_Wallet_Account(_walletAccountAddress);
        HyperAGI_Agent agentAddress = HyperAGI_Agent(_agentAddress);

        address _GasFeeCollectionWallet = walletAccountAddress._GasFeeCollectionWallet();

        require(_GasFeeCollectionWallet != address(0), "not set GasFeeCollectionWallet");

        (uint256 tokenId, , , , address erc721Address, , , ) = agentAddress.getAgent(sid);

        require(IERC721(_agentPOPNFTAddress).ownerOf(tokenId) == msg.sender, "not owner");

        uint256 gasFee = HyperAGI_Transaction_Cfg(_transactionCfgAddress).getGasFee("mintNFT");

        require(msg.value == gasFee, "not enough gas fee");

        if (gasFee > 0) {
            HyperAGI_Wallet_Account(_walletAccountAddress).addAmount(gasFee);
        }

        Agent_721(erc721Address).safeMint(msg.sender, tokenURI);
    }

    function mint1155(bytes32 sid, uint256 id, uint256 amount, string memory tokenURI) public payable {
        HyperAGI_Wallet_Account walletAccountAddress = HyperAGI_Wallet_Account(_walletAccountAddress);
        HyperAGI_Agent agentAddress = HyperAGI_Agent(_agentAddress);

        address _GasFeeCollectionWallet = walletAccountAddress._GasFeeCollectionWallet();

        require(_GasFeeCollectionWallet != address(0), "not set GasFeeCollectionWallet");

        (uint256 tokenId, , , , , address erc1155Address, , ) = agentAddress.getAgent(sid);

        require(IERC721(_agentPOPNFTAddress).ownerOf(tokenId) == msg.sender, "not owner");

        uint256 gasFee = HyperAGI_Transaction_Cfg(_transactionCfgAddress).getGasFee("mintNFT");

        require(msg.value == gasFee, "not enough gas fee");

        if (gasFee > 0) {
            HyperAGI_Wallet_Account(_walletAccountAddress).addAmount(gasFee);
        }

        Agent_1155(erc1155Address).mint(msg.sender, id, amount, tokenURI, "");
    }
}

contracts/node/HyperAGI_Miner_NFT_Pledge.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.1;
import "@openzeppelin/contracts/utils/Strings.sol";

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "../HyperAGI_Roles_Cfg.sol";

import "../utils/StrUtil.sol";

import "./../HyperAGI_Storage.sol";

import "./../node/HyperAGI_Node_Mgr.sol";

contract HyperAGI_Miner_NFT_Pledge is OwnableUpgradeable {
    using Strings for *;
    using StrUtil for *;

    address public _rolesCfgAddress;
    address public _storageAddress;
    address public _721Address;
    uint256 public _pledgeTime;
    address public _nodeMgrAddress;

    function initialize(address onlyOwner) public initializer {
        __Ownable_init(onlyOwner);
        _pledgeTime = 365 days;
    }

    function setRolesCfgAddress(address rolesCfgAddress) public onlyOwner {
        _rolesCfgAddress = rolesCfgAddress;
    }

    function setStorageAddress(address storageAddress) public onlyOwner {
        _storageAddress = storageAddress;
    }

    function set721Address(address address721) public onlyOwner {
        _721Address = address721;
    }

    function setNodeMgrAddress(address nodeMgrAddress) public onlyOwner {
        _nodeMgrAddress = nodeMgrAddress;
    }

    function setPledgeTime(uint256 pledgeTime) public onlyOwner {
        _pledgeTime = pledgeTime;
    }

    function setContractAddress(address[] memory contractaddressArray) public onlyOwner {
        _rolesCfgAddress = contractaddressArray[0];
        _storageAddress = contractaddressArray[1];
        _721Address = contractaddressArray[2];
        _nodeMgrAddress = contractaddressArray[3];
    }

    event evePledge(address account, address tokenAddress, uint256 tokenId, uint256 allowedRedemptionTime);
    event eveRedemption(address account, address tokenAddress, uint256 tokenId);

    function pledge(uint256 tokenId) public {
        HyperAGI_Storage storageAddress = HyperAGI_Storage(_storageAddress);
        IERC721 erc721 = IERC721(_721Address);
        require(erc721.ownerOf(tokenId) == msg.sender, "not owner");
        string memory key = storageAddress.genKey(_721Address.toHexString(), tokenId);
        storageAddress.setAddress(key, msg.sender);

        string memory allowedRedemptionTimeKey = string(abi.encodePacked(key, "allowedRedemptionTime"));

        uint256 allowedRedemptionTime = storageAddress.getUint(allowedRedemptionTimeKey);

        if (allowedRedemptionTime == 0) {
            storageAddress.setUint(allowedRedemptionTimeKey, block.timestamp + _pledgeTime);
            allowedRedemptionTime = block.timestamp + _pledgeTime;
        }
        erc721.transferFrom(msg.sender, address(this), tokenId);

        string memory accountKey = msg.sender.toHexString();

        uint256 pledgeNum = storageAddress.getUint(accountKey);

        storageAddress.setUint(accountKey, pledgeNum + 1);

        emit evePledge(msg.sender, _721Address, tokenId, allowedRedemptionTime);
    }

    function redemption(uint256 tokenId) public {
        HyperAGI_Storage storageAddress = HyperAGI_Storage(_storageAddress);

        HyperAGI_Node_Mgr nodeMgrAddress = HyperAGI_Node_Mgr(_nodeMgrAddress);

        IERC721 erc721 = IERC721(_721Address);
        string memory key = storageAddress.genKey(_721Address.toHexString(), tokenId);
        address ownerAddress = storageAddress.getAddress(key);
        require(ownerAddress == msg.sender, "not owner");

        string memory allowedRedemptionTimeKey = string(abi.encodePacked(key, "allowedRedemptionTime"));

        uint256 allowedRedemptionTime = storageAddress.getUint(allowedRedemptionTimeKey);

        require(block.timestamp > allowedRedemptionTime, "not allowed time");

        erc721.transferFrom(address(this), msg.sender, tokenId);

        string memory accountKey = msg.sender.toHexString();

        uint256 pledgeNum = storageAddress.getUint(accountKey);

        uint256 nodeNum = nodeMgrAddress.getAccountNodeNum(msg.sender);

        require(nodeNum <= pledgeNum - 1, "The node must be offline before NFT assets can be redeemed");

        storageAddress.setUint(accountKey, pledgeNum - 1);

        emit eveRedemption(msg.sender, _721Address, tokenId);
    }

    function getAccountPledgeNum(address account) public view returns (uint256) {
        HyperAGI_Storage storageAddress = HyperAGI_Storage(_storageAddress);

        string memory accountKey = account.toHexString();
        return storageAddress.getUint(accountKey);
    }
}

contracts/node/HyperAGI_Node_Mgr.sol.content:
pragma solidity ^0.8.2;

import "@openzeppelin/contracts/utils/Strings.sol";

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";

import {StrUtil} from "../utils/StrUtil.sol";

import "./../HyperAGI_Storage.sol";

import "./HyperAGI_Miner_NFT_Pledge.sol";

import "./HyperAGI_Node_Type.sol";

contract HyperAGI_Node_Mgr is OwnableUpgradeable {
    using Strings for *;
    using StrUtil for *;

    address public _rolesCfgAddress;
    address public _nodeTypeAddress;
    address public _storageAddress;
    address public _minerNFTPledgeAddress;
    uint32 public _totalNum;
    uint32 public _activeNum;

    struct Node {
        address incomeAddress;
        string ip; //Node public network IP
        uint256[] uint256Array; //id,nodeType,cpuNum,memoryNum,diskNum,cudaNum,videoMemory
        bool isOffline;
    }

    event eveSave(uint256 id);

    event eveDelete(uint256 id);

    function initialize(address onlyOwner) public initializer {
        __Ownable_init(onlyOwner);
    }

    function setNodeTypeAddress(address nodeTypeAddress) public onlyOwner {
        _nodeTypeAddress = nodeTypeAddress;
    }

    function setRolesCfgAddress(address rolesCfgAddress) public onlyOwner {
        _rolesCfgAddress = rolesCfgAddress;
    }

    function setStorageAddress(address storageAddress) public onlyOwner {
        _storageAddress = storageAddress;
    }

    function setMinerNFTPledgeAddress(address minerNFTPledgeAddress) public onlyOwner {
        _minerNFTPledgeAddress = minerNFTPledgeAddress;
    }

    function setContractAddress(address[] memory contractaddressArray) public onlyOwner {
        _rolesCfgAddress = contractaddressArray[0];
        _nodeTypeAddress = contractaddressArray[1];
        _storageAddress = contractaddressArray[2];
        _minerNFTPledgeAddress = contractaddressArray[3];
    }

    function addNode(address incomeAddress, string memory ip, uint256[] memory hardwareInfos) public returns (uint256) {
        HyperAGI_Storage storageAddress = HyperAGI_Storage(_storageAddress);
        HyperAGI_Miner_NFT_Pledge minerNFTPledgeAddress = HyperAGI_Miner_NFT_Pledge(_minerNFTPledgeAddress);

        uint256 pledgeNum = minerNFTPledgeAddress.getAccountPledgeNum(incomeAddress);

        string memory accountKey = incomeAddress.toHexString();

        uint256 nodeNum = storageAddress.getUint(accountKey);

        require(nodeNum + 1 >= pledgeNum, "The amount of pledged NFT is insufficient, please pledge the NFT first");

        require(!storageAddress.getBool(ip), "ip already exists");

        HyperAGI_Node_Type nodeType = HyperAGI_Node_Type(_nodeTypeAddress);

        uint256 nodeTypeId = nodeType.getNodeTypeId(hardwareInfos[0], hardwareInfos[1], hardwareInfos[2], hardwareInfos[3], hardwareInfos[4]);

        require(nodeTypeId > 0, "not found node type");

        uint256 id = storageAddress.getNextId();

        add(id, nodeTypeId, incomeAddress, ip, hardwareInfos);

        storageAddress.setUint(accountKey, nodeNum + 1);

        return id;
    }

    function add(uint256 id, uint256 nodeTypeId, address incomeAddress, string memory ip, uint256[] memory hardwareInfos) private {
        HyperAGI_Storage storageAddress = HyperAGI_Storage(_storageAddress);

        uint256 count = storageAddress.getUint("count");

        storageAddress.setBool(ip, true);

        storageAddress.setUint("count", count + 1);

        storageAddress.setUint(storageAddress.genKey("nodeTypeId", id), nodeTypeId);

        storageAddress.setUint(storageAddress.genKey("cpuNum", id), hardwareInfos[0]);

        storageAddress.setUint(storageAddress.genKey("memoryNum", id), hardwareInfos[1]);

        storageAddress.setUint(storageAddress.genKey("diskNum", id), hardwareInfos[2]);

        storageAddress.setUint(storageAddress.genKey("cudaNum", id), hardwareInfos[3]);

        storageAddress.setUint(storageAddress.genKey("videoMemory", id), hardwareInfos[4]);

        storageAddress.setAddress(storageAddress.genKey("incomeAddress", id), incomeAddress);

        storageAddress.setString(storageAddress.genKey("ip", id), ip);

        storageAddress.setUintArray("idList", id);

        emit eveSave(id);
    }

    function getNode(uint256 id) public view returns (address, string memory, uint256[] memory, bool) {
        HyperAGI_Storage storageAddress = HyperAGI_Storage(_storageAddress);
        string memory ip = storageAddress.getString(storageAddress.genKey("ip", id));

        require(bytes(ip).length > 0, "not found");

        address incomeAddress = storageAddress.getAddress(storageAddress.genKey("incomeAddress", id));

        uint256[] memory uint256Array = new uint256[](7);

        uint256Array[0] = id;
        uint256Array[1] = storageAddress.getUint(storageAddress.genKey("nodeTypeId", id));
        uint256Array[2] = storageAddress.getUint(storageAddress.genKey("cpuNum", id));
        uint256Array[3] = storageAddress.getUint(storageAddress.genKey("memoryNum", id));
        uint256Array[4] = storageAddress.getUint(storageAddress.genKey("diskNum", id));
        uint256Array[5] = storageAddress.getUint(storageAddress.genKey("cudaNum", id));
        uint256Array[6] = storageAddress.getUint(storageAddress.genKey("videoMemory", id));

        bool isOffline = storageAddress.getBool(storageAddress.genKey("isOffine", id));

        return (incomeAddress, ip, uint256Array, isOffline);
    }

    function getNodeObj(uint256 id) public view returns (Node memory) {
        (address incomeAddress, string memory ip, uint256[] memory uint256Array, bool isOffline) = getNode(id);

        Node memory node = Node({incomeAddress: incomeAddress, ip: ip, uint256Array: uint256Array, isOffline: isOffline});

        return node;
    }

    function deleteNode(uint256 id) public {
        require(HyperAGI_Roles_Cfg(_rolesCfgAddress).hasAdminRole(msg.sender), "not admin role");

        HyperAGI_Storage storageAddress = HyperAGI_Storage(_storageAddress);

        string memory ip = storageAddress.getString(storageAddress.genKey("ip", id));

        require(bytes(ip).length > 0, "not found");

        storageAddress.setString(storageAddress.genKey("ip", id), "");

        uint256 count = storageAddress.getUint("count");

        storageAddress.setUint("count", count - 1);

        uint256[] memory idList = storageAddress.getUintArray("idList");

        for (uint i = 0; i < idList.length; i++) {
            if (idList[i] == id) {
                storageAddress.removeStringArray("idList", i);
                break;
            }
        }

        emit eveDelete(id);
    }

    function getStatisticalIndex() public view returns (uint256, uint256, uint256) {
        HyperAGI_Storage storageAddress = HyperAGI_Storage(_storageAddress);
        uint256 count = storageAddress.getUint("count");
        uint256 totalNum = storageAddress.getUint("totalNum");
        uint256 activeNum = storageAddress.getUint("activeNum");

        return (count, totalNum, activeNum);
    }

    function setStatisticalIndex(uint256 totalNum, uint256 activeNum) public {
        require(HyperAGI_Roles_Cfg(_rolesCfgAddress).hasAdminRole(msg.sender), "not admin role");

        HyperAGI_Storage storageAddress = HyperAGI_Storage(_storageAddress);

        storageAddress.setUint("totalNum", totalNum);
        storageAddress.setUint("activeNum", activeNum);
    }

    function getIdByIndex(uint256 index) public view returns (uint256) {
        HyperAGI_Storage storageAddress = HyperAGI_Storage(_storageAddress);

        uint256[] memory ids = storageAddress.getUintArray("idList");

        if (index + 1 > ids.length) {
            return 0;
        }

        return ids[index];
    }

    function setIdList(uint256[] memory idList) public onlyOwner {
        HyperAGI_Storage storageAddress = HyperAGI_Storage(_storageAddress);

        storageAddress.setUintArray("idList", idList);
    }

    function updateStatus(uint256 nodeId, bool isOffline) public {
        require(HyperAGI_Roles_Cfg(_rolesCfgAddress).hasAdminRole(msg.sender), "not admin role");
        HyperAGI_Storage storageAddress = HyperAGI_Storage(_storageAddress);

        HyperAGI_Miner_NFT_Pledge minerNFTPledgeAddress = HyperAGI_Miner_NFT_Pledge(_minerNFTPledgeAddress);

        address incomeAddress = storageAddress.getAddress(storageAddress.genKey("incomeAddress", nodeId));

        uint256 pledgeNum = minerNFTPledgeAddress.getAccountPledgeNum(incomeAddress);

        string memory accountKey = incomeAddress.toHexString();

        storageAddress.setBool(storageAddress.genKey("isOffline", nodeId), isOffline);

        uint256 nodeNum = storageAddress.getUint(accountKey);

        if (isOffline) {
            storageAddress.setUint(accountKey, nodeNum - 1);
        } else {
            require(nodeNum + 1 >= pledgeNum, "The amount of pledged NFT is insufficient, please pledge the NFT first");
            storageAddress.setUint(accountKey, nodeNum + 1);
        }

        emit eveSave(nodeId);
    }

    function getAccountNodeNum(address account) public view returns (uint256) {
        HyperAGI_Storage storageAddress = HyperAGI_Storage(_storageAddress);
        string memory accountKey = account.toHexString();
        return storageAddress.getUint(accountKey);
    }
}

contracts/node/HyperAGI_Node_Type.sol.content:
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Strings.sol";

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "../utils/StrUtil.sol";

import "./../HyperAGI_Roles_Cfg.sol";
import "./../HyperAGI_Storage.sol";

import "./../node/HyperAGI_Node_Mgr.sol";

contract HyperAGI_Node_Type is OwnableUpgradeable {
    address public _rolesCfgAddress;
    address public _storageAddress;

    using Strings for *;
    using StrUtil for *;

    struct NodeType {
        uint256 id;
        uint256 orderNum;
        string name;
        uint256 cpuNum;
        uint256 memoryNum;
        uint256 diskNum;
        uint256 cudaNum;
        uint256 videoMemory;
        string coverImage;
        string frameRate;
        string remark;
    }

    function initialize(address onlyOwner) public initializer {
        __Ownable_init(onlyOwner);
    }

    event eveSave(uint256 id);

    event eveDelete(uint256 id);

    uint256[] public _ids;

    function setRolesCfgAddress(address rolesCfgAddress) public onlyOwner {
        _rolesCfgAddress = rolesCfgAddress;
    }

    function setStorageAddress(address storageAddress) public onlyOwner {
        _storageAddress = storageAddress;
    }

    function setContractAddress(address[] memory contractaddressArray) public onlyOwner {
        _storageAddress = contractaddressArray[0];
        _rolesCfgAddress = contractaddressArray[1];
    }

    function addNodeType(uint256 orderNum, string memory name, uint256 cpuNum, uint256 memoryNum, uint256 diskNum, uint256 cudaNum, uint256 videoMemory, string memory coverImage, string memory frameRate, string memory remark) public returns (uint256) {
        require(HyperAGI_Roles_Cfg(_rolesCfgAddress).hasAdminRole(msg.sender), "not admin role");

        HyperAGI_Storage storageAddress = HyperAGI_Storage(_storageAddress);
        uint256 id = storageAddress.getNextId();

        _ids.push(id);

        storageAddress.setUint(storageAddress.genKey("orderNum", id), orderNum);

        storageAddress.setString(storageAddress.genKey("name", id), name);
        storageAddress.setUint(storageAddress.genKey("cpuNum", id), cpuNum);
        storageAddress.setUint(storageAddress.genKey("memoryNum", id), memoryNum);
        storageAddress.setUint(storageAddress.genKey("diskNum", id), diskNum);
        storageAddress.setUint(storageAddress.genKey("cudaNum", id), cudaNum);
        storageAddress.setUint(storageAddress.genKey("videoMemory", id), videoMemory);
        storageAddress.setString(storageAddress.genKey("coverImage", id), coverImage);
        storageAddress.setString(storageAddress.genKey("frameRate", id), frameRate);
        storageAddress.setString(storageAddress.genKey("remark", id), remark);
        emit eveSave(id);
        return id;
    }

    function updateNodeType(uint256 id, uint256 orderNum, string memory name, uint256 cpuNum, uint256 memoryNum, uint256 diskNum, uint256 cudaNum, uint256 videoMemory, string memory coverImage, string memory frameRate, string memory remark) public {
        require(HyperAGI_Roles_Cfg(_rolesCfgAddress).hasAdminRole(msg.sender), "not admin role");

        HyperAGI_Storage storageAddress = HyperAGI_Storage(_storageAddress);

        string memory _name = storageAddress.getString(storageAddress.genKey("name", id));

        require(bytes(_name).length > 0, "not found");

        storageAddress.setUint(storageAddress.genKey("orderNum", id), orderNum);

        storageAddress.setString(storageAddress.genKey("name", id), name);
        storageAddress.setUint(storageAddress.genKey("cpuNum", id), cpuNum);
        storageAddress.setUint(storageAddress.genKey("memoryNum", id), memoryNum);
        storageAddress.setUint(storageAddress.genKey("diskNum", id), diskNum);
        storageAddress.setUint(storageAddress.genKey("cudaNum", id), cudaNum);
        storageAddress.setUint(storageAddress.genKey("videoMemory", id), videoMemory);
        storageAddress.setString(storageAddress.genKey("coverImage", id), coverImage);
        storageAddress.setString(storageAddress.genKey("frameRate", id), frameRate);
        storageAddress.setString(storageAddress.genKey("remark", id), remark);

        emit eveSave(id);
    }

    function getNodeType(uint256 id) public view returns (uint256[] memory, string[] memory) {
        HyperAGI_Storage storageAddress = HyperAGI_Storage(_storageAddress);

        string[] memory strArray = new string[](4);
        uint256[] memory uint256Array = new uint256[](7);

        strArray[0] = storageAddress.getString(storageAddress.genKey("name", id));

        require(bytes(strArray[0]).length > 0, "not found");

        strArray[1] = storageAddress.getString(storageAddress.genKey("coverImage", id));
        strArray[2] = storageAddress.getString(storageAddress.genKey("frameRate", id));
        strArray[3] = storageAddress.getString(storageAddress.genKey("remark", id));

        uint256Array[0] = id;
        uint256Array[1] = storageAddress.getUint(storageAddress.genKey("orderNum", id));

        uint256Array[2] = storageAddress.getUint(storageAddress.genKey("cpuNum", id));
        uint256Array[3] = storageAddress.getUint(storageAddress.genKey("memoryNum", id));

        uint256Array[4] = storageAddress.getUint(storageAddress.genKey("diskNum", id));

        uint256Array[5] = storageAddress.getUint(storageAddress.genKey("nodeType", id));

        uint256Array[6] = storageAddress.getUint(storageAddress.genKey("videoMemory", id));

        return (uint256Array, strArray);
    }

    function deleteNodeType(uint256 id) public {
        require(HyperAGI_Roles_Cfg(_rolesCfgAddress).hasAdminRole(msg.sender), "not admin role");

        HyperAGI_Storage storageAddress = HyperAGI_Storage(_storageAddress);

        string memory name = storageAddress.getString(storageAddress.genKey("name", id));

        require(bytes(name).length > 0, "not found");

        storageAddress.setString(storageAddress.genKey("name", id), "");

        emit eveDelete(id);
    }

    function getNodeTypeList() public view returns (NodeType[] memory) {
        NodeType[] memory list = new NodeType[](_ids.length);
        for (uint i = 0; i < _ids.length; i++) {
            uint256 id = _ids[i];
            (uint256[] memory uint256Array, string[] memory strArray) = getNodeType(id);
            list[i] = NodeType(uint256Array[0], uint256Array[1], strArray[0], uint256Array[2], uint256Array[3], uint256Array[4], uint256Array[5], uint256Array[6], strArray[1], strArray[2], strArray[3]);
        }

        return list;
    }

    function getNodeTypeId(uint256 cpuNum, uint256 memoryNum, uint256 diskNum, uint256 cudaNum, uint256 videoMemory) public view returns (uint256) {
        NodeType[] memory sortedData = getNodeTypeList();
        uint n = sortedData.length;

        if (n > 1) {
            for (uint i = 0; i < n - 1; i++) {
                for (uint j = 0; j < n - i - 1; j++) {
                    if (sortedData[j].orderNum < sortedData[j + 1].orderNum) {
                        // switch data elements
                        (sortedData[j], sortedData[j + 1]) = (sortedData[j + 1], sortedData[j]);
                    }
                }
            }
        }

        uint256 min = 0;

        for (uint i = 0; i < n; i++) {
            NodeType memory nodeType = sortedData[i];

            if (cpuNum >= nodeType.cpuNum && memoryNum >= nodeType.memoryNum && diskNum >= nodeType.diskNum && cudaNum >= nodeType.cudaNum && videoMemory >= nodeType.videoMemory) {
                min = nodeType.id;

                break;
            }
        }

        return min;
    }
}

contracts/utils/StrUtil.sol.content:
/*
 * @title String & slice utility library for Solidity contracts.
 * @author Nick Johnson <arachnid@notdot.net>
 *
 * @dev Functionality in this library is largely implemented using an
 *      abstraction called a 'slice'. A slice represents a part of a string -
 *      anything from the entire string to a single character, or even no
 *      characters at all (a 0-length slice). Since a slice only has to specify
 *      an offset and a length, copying and manipulating slices is a lot less
 *      expensive than copying and manipulating the strings they reference.
 *
 *      To further reduce gas costs, most functions on slice that need to return
 *      a slice modify the original one instead of allocating a new one; for
 *      instance, `s.split(".")` will return the text up to the first '.',
 *      modifying s to only contain the remainder of the string after the '.'.
 *      In situations where you do not want to modify the original slice, you
 *      can make a copy first with `.copy()`, for example:
 *      `s.copy().split(".")`. Try and avoid using this idiom in loops; since
 *      Solidity has no memory management, it will result in allocating many
 *      short-lived slices that are later discarded.
 
 *      Functions that return two slices come in two versions: a non-allocating
 *      version that takes the second slice as an argument, modifying it in
 *      place, and an allocating version that allocates and returns the second
 *      slice; see `nextRune` for example.
 *
 *      Functions that have to copy string data will return strings rather than
 *      slices; these can be cast back to slices for further processing if
 *      required.
 *
 *      For convenience, some functions are provided with non-modifying
 *      variants that create a new slice and return both; for instance,
 *      `s.splitNew('.')` leaves s unmodified, and returns two values
 *      corresponding to the left and right parts of the string.
 */

pragma solidity ^0.8.0;

library StrUtil {
    struct slice {
        uint256 _len;
        uint256 _ptr;
    }

    function memcpy(uint256 dest, uint256 src, uint256 len) private pure {
        // Copy word-length chunks while possible
        for (; len >= 32; len -= 32) {
            assembly {
                mstore(dest, mload(src))
            }
            dest += 32;
            src += 32;
        }

        // Copy remaining bytes
        uint256 mask = type(uint256).max;
        if (len > 0) {
            mask = 256 ** (32 - len) - 1;
        }
        assembly {
            let srcpart := and(mload(src), not(mask))
            let destpart := and(mload(dest), mask)
            mstore(dest, or(destpart, srcpart))
        }
    }

    /*
     * @dev Returns a slice containing the entire string.
     * @param self The string to make a slice from.
     * @return A newly allocated slice containing the entire string.
     */
    function toSlice(string memory self) internal pure returns (slice memory) {
        uint256 ptr;
        assembly {
            ptr := add(self, 0x20)
        }
        return slice(bytes(self).length, ptr);
    }

    /*
     * @dev Returns the length of a null-terminated bytes32 string.
     * @param self The value to find the length of.
     * @return The length of the string, from 0 to 32.
     */
    function len(bytes32 self) internal pure returns (uint256) {
        uint256 ret;
        if (self == 0) return 0;
        if (uint256(self) & type(uint128).max == 0) {
            ret += 16;
            self = bytes32(uint256(self) / 0x100000000000000000000000000000000);
        }
        if (uint256(self) & type(uint64).max == 0) {
            ret += 8;
            self = bytes32(uint256(self) / 0x10000000000000000);
        }
        if (uint256(self) & type(uint32).max == 0) {
            ret += 4;
            self = bytes32(uint256(self) / 0x100000000);
        }
        if (uint256(self) & type(uint16).max == 0) {
            ret += 2;
            self = bytes32(uint256(self) / 0x10000);
        }
        if (uint256(self) & type(uint8).max == 0) {
            ret += 1;
        }
        return 32 - ret;
    }

    /*
     * @dev Returns a slice containing the entire bytes32, interpreted as a
     *      null-terminated utf-8 string.
     * @param self The bytes32 value to convert to a slice.
     * @return A new slice containing the value of the input argument up to the
     *         first null.
     */
    function toSliceB32(bytes32 self) internal pure returns (slice memory ret) {
        // Allocate space for `self` in memory, copy it there, and point ret at it
        assembly {
            let ptr := mload(0x40)
            mstore(0x40, add(ptr, 0x20))
            mstore(ptr, self)
            mstore(add(ret, 0x20), ptr)
        }
        ret._len = len(self);
    }

    /*
     * @dev Returns a new slice containing the same data as the current slice.
     * @param self The slice to copy.
     * @return A new slice containing the same data as `self`.
     */
    function copy(slice memory self) internal pure returns (slice memory) {
